(function(){Polymer({is:'px-app-header',properties:{fixed:{type:Boolean,value:!1,observer:'_fixedChanged'},_condenses:{type:Boolean,value:!0},_scrollEffect:{type:String,value:'px-app-header-scroll-effect'}},ready(){Polymer.dom(this.root).querySelector('app-header').addEventListener('px-app-header-scroll-reset',()=>{this._setHeaderFixedState()})},_setHeaderFixedState(){this._condenses=!this.fixed,this._scrollEffect=this.fixed?'':'px-app-header-scroll-effect'},_fixedChanged(){Polymer.dom(this.root).querySelector('app-header').willCondense()||this._setHeaderFixedState()}})})();